package com.example.database;public enum Block {    ID(Integer.valueOf(0), "id"),    TURN(Integer.valueOf(1), "turn"),    NAME(Integer.valueOf(2), "name"),    COMPOSITION_ID(Integer.valueOf(3), "composition_id"),    DESCRIPTION(Integer.valueOf(4), "description"),    FILE_PATH(Integer.valueOf(5), "file_path"),    THUMB_PATH(Integer.valueOf(6), "thumbnail_path"),    PROJECT_ID(Integer.valueOf(7), "project_id");    private Integer num;    private String name;    private Block(Integer num, String name){        this.num = num;        this.name = name;    }    public Integer getNumber(){        return this.num;    }    public String getName(){        return this.name;    }}