package com.example.database;public enum Table {    PROJECT(Integer.valueOf(0), "project"),    BLOCK(Integer.valueOf(1), "block"),    COMPOSITION(Integer.valueOf(2), "composition"),    SAMPLE(Integer.valueOf(3), "sample");    private Integer num;    private String name;    private Table(Integer num, String name){        this.num = num;        this.name = name;    }    public Integer getNumber(){        return this.num;    }    public String getName(){        return this.name;    }}